// src/components/Navbar.js
import React, { useState } from 'react';

function Navbar(props) {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => setIsDropdownOpen(!isDropdownOpen);

  return (
    <nav className="bg-white shadow-md p-4">
      <div className="container mx-auto flex items-center justify-between">
        {/* Title */}
        <a href="/" className="text-2xl font-bold text-red-600 hover:text-red-800">
          {props.title}
        </a>

        {/* Search Bar */}
        <div className="flex-grow mx-4">
          <input
            type="text"
            placeholder="Search..."
            className="w-full p-2 border border-gray-300 rounded-md focus:outline-none focus:border-red-500"
          />
        </div>
        {/* Navigation Links */}
        <div className="mx-5 hidden md:flex space-x-4">
          <a href="/" className="text-gray-700 hover:text-gray-900">Home</a>
          <a href="/listings" className="text-gray-700 hover:text-gray-900">Listings</a>
          <a href="/contact" className="text-gray-700 hover:text-gray-900">Contact Us</a>
        </div>


        {/* Login/Signup Dropdown */}
        <div className="relative">
          <button
            onClick={toggleDropdown}
            className="text-gray-700 hover:text-gray-900 focus:outline-none flex items-center"
          >
            <span>Login / Signup</span>
            <svg
              className="w-5 h-5 inline-block ml-2"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
            </svg>
          </button>
          {isDropdownOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white border border-gray-300 rounded-md shadow-lg">
              <a href="/login" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Login</a>
              <a href="/signup" className="block px-4 py-2 text-gray-700 hover:bg-gray-100">Signup</a>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
