import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navbar({ title, user, onLogout }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen((prev) => !prev);
  };

  return (
    <nav className="bg-white shadow-md p-4">
      <div className="container mx-auto flex items-center justify-between">
        <Link to="/" className="mr-20 text-2xl font-bold text-red-600 hover:text-red-800">
          {title}
        </Link>

        <div className="flex border gap-2 border-gray-300 rounded-full py-2 px-4 shadow-md shadow-gray-200">
          <div>Anywhere</div>
          <div className="border-l border-gray-300"></div>
          <div>Any week</div>
          <div className="border-l border-gray-300"></div>
          <div>Add guests</div>
          <button>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="size-6">
              <path strokeLinecap="round" strokeLinejoin="round" d="m15.75 15.75-2.489-2.489m0 0a3.375 3.375 0 1 0-4.773-4.773 3.375 3.375 0 0 0 4.774 4.774ZM21 12a9 9 0 1 1-18 0 9 9 0 0 1 18 0Z" />
            </svg>
          </button>
        </div>

        <div className="relative">
          {user ? (
            <div className="flex items-center space-x-4">
              <span className="text-gray-700">{user.name}</span>
              <button onClick={onLogout} className="text-red-600 hover:text-red-800">
                Logout
              </button>
            </div>
          ) : (
            <div className="flex items-center">
              <button onClick={toggleDropdown} className="flex items-center border gap-2 border-gray-300 rounded-full py-2 px-4">
                {/* Hamburger icon */}
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="size-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5" />
                </svg>
                {/* User icon */}
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="size-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M17.982 18.725A7.488 7.488 0 0 0 12 15.75a7.488 7.488 0 0 0-5.982 2.975m11.963 0a9 9 0 1 0-11.963 0m11.963 0A8.966 8.966 0 0 1 12 21a8.966 8.966 0 0 1-5.982-2.275M15 9.75a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
                </svg>
              </button>

              {dropdownOpen && (
                <div className="absolute right-0 mt-2 w-48 bg-white rounded-md border border-gray-300 shadow-lg z-10">
                  <Link to="/login" className="block px-4 py-2 text-gray-700 hover:bg-red-100 transition-colors duration-200 border-b border-gray-300 last:border-b-0">Login</Link>
                  <Link to="/register" className="block px-4 py-2 text-gray-700 hover:bg-red-100 transition-colors duration-200">Sign Up</Link>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
