import React,{useState,use} from 'react'
import { Link } from 'react-router-dom'

export default function RegisterPage() {
  const [signupInfo, setSignupInfo] = useState({
    name:'',
    email:'',
    password:''
  });
  const navigate = useNavigate();
  const handleChange=(e)=>{
    const {name,value} = e.target;
    console.log(name,value);
    setSignupInfo({ ...signupInfo, [name]: value }); 
  }

  const handleSubmit = async (e)=>{
    e.preventDefault();
    console.log('Submitting:', signupInfo);
    const url='http://localhost:3000/au/signup';

    try {
      const response = await fetch(url, {
          method: "POST",
          headers: {
              "Content-Type": "application/json" 
          },
          body: JSON.stringify(signupInfo) 
      });

      if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json(); 
      console.log(result); 
      // Redirect to login page on successful signup
      if (result.success) {
        navigate('/au/login'); // Redirect to login page
      }
  } catch (error) {
      console.error("Error:", error); 
  }
  }
  return (
    <div>
      <div className="min-h-screen bg-gray-100 pt-16"> 
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-sm mx-auto mt-8">
      <h1 className="text-3xl font-semibold text-center mb-6">Register</h1>

        <form id="signup-form" className="space-y-4" onSubmit={handleSubmit}>
          <input name="name" onChange={handleChange} required type="text" placeholder="John Doe" className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-red-600" />
          <input name="email" onChange={handleChange} required type="email" placeholder="your@email.com" className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-red-600" />
          <input name="password" onChange={handleChange} required type="password" placeholder="password" className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-red-600" />
          <button type="submit" className="w-full bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-600">
          Register
          </button>
        </form>

        <div className="text-center mt-4 text-gray-600">
          Already a member?{" "}
          <Link className="text-black underline" to="/login">
           Login
          </Link>
        </div>

      </div>
    </div>
    </div>
  )
}
