import React,{useState} from 'react'
import { Link } from 'react-router-dom'

export default function RegisterPage() {
  const [loginInfo, setLoginInfo] = useState({
    name:'',
    email:'',
    password:''
  });
  const handleChange=(e)=>{
    const {name,name_value} = e.target;
    console.log(name,value);
    const copyLoginInfo = {...loginInfo};
    copyLoginInfo[name]=value;
    setLoginInfo(copyLoginInfo);
  }
  console.log()

  return (
    <div>
      <div className="min-h-screen bg-gray-100 pt-16"> {/* Add pt-16 to push content below the navbar */}
    <div className="bg-white p-8 rounded-lg shadow-lg max-w-sm mx-auto mt-8">
      <h1 className="text-3xl font-semibold text-center mb-6">Register</h1>

        <form id="signup-form" className="space-y-4">
          <input onChange={handleChange} type="text" placeholder="John Doe" className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-red-600"/>
          <input onChange={handleChange} type="email" placeholder="your@email.com" className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-red-600"/>
          <input onChange={handleChange} type="password" placeholder="password" className="w-full border border-gray-300 rounded-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-red-600"/>
          <button type="submit" className="w-full bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-600">
          Register
          </button>
        </form>

        <div className="text-center mt-4 text-gray-600">
          Already a member?{" "}
          <Link className="text-black underline" to="/login">
           Login
          </Link>
        </div>

      </div>
    </div>
    </div>
  )
}
